# Calculate team strength based on points and type
def calculate_team_strength(team_name,points, played_games, type_weight):

    home_perc, away_pec = get_last_5_home_away_wins(team_name, played_games)


    return float(home_perc) * float(type_weight), float(away_pec) * float(type_weight)


# Simulate one match result based on team strengths
def simulate_match(home_strength, away_strength, home_type, away_type):
    total_strength = home_strength + away_strength
    draw_probability = 0.28  # Base draw probability

    total_no_draw = 1 - draw_probability

    home_win_prob = total_no_draw * (float(home_type) / 5)
    away_win_prob = total_no_draw * (float(away_type) / 5)
    total_prob = home_win_prob + away_win_prob


    # Probabilities of win, draw, loss
    if total_strength == 0 : total_strength = 1

    home_win_prob = home_win_prob * ( total_no_draw / total_prob)
    away_win_prob = away_win_prob * ( total_no_draw / total_prob)
    #total_1 = home_win_prob + away_win_prob
    #print(f" {home_win_prob:.2%}, {away_win_prob:.2%}  {total_1:.2%}" )

    # Randomly determine match outcome
    result = random.random()
    if result < home_win_prob:
        return "home_win"
    elif result < home_win_prob + draw_probability:
        return "draw"
    else:
        return "away_win"


def simulate_season(teams, matches, country, n_iterations=50000 ):
    # Default positions if none are provided
    positions = leagues_positions.get(country)
    if country is None:
        positions = {
            "champion": {"start": 0, "end": 1, "description": "Campeao"},  # Champion (first place)
            "layer1": {"start": 0, "end": 6, "description": "Libertadores"},  # Top 6 positions
            "layer2": {"start": 6, "end": 8, "description": "preLibertadores"},  # Top 12 positions
            "layer3": {"start": 6, "end": 12, "description": "sulAmericana"},  # Top 12 positions
            "relegated": {"start": -4, "end": None, "description": "Rebaixado"}  # Bottom 4 teams
        }

    # Initialize counters for each team's result statistics
    results = defaultdict(lambda: {key: 0 for key in positions.keys()})

    # Run the simulation n_iterations times
    for _ in range(n_iterations):
        # Make a deep copy of teams' standings
        simulated_teams = {team["shortName"]: team.copy() for team in teams}

        # Simulate each match
        for match in matches:
            home_team, away_team = match["home"], match["away"]
            home_strength = simulated_teams[home_team]["strength_home"]
            away_strength = simulated_teams[away_team]["strength_away"]
            home_type = simulated_teams[home_team]["type"]
            away_type = simulated_teams[away_team]["type"]


            result = simulate_match(home_strength, away_strength, home_type, away_type)

            # Update the points table based on match result
            if result == "home_win":
                simulated_teams[home_team]["pts"] = int(simulated_teams[home_team]["pts"]) + 3
            elif result == "away_win":
                simulated_teams[away_team]["pts"] = int(simulated_teams[away_team]["pts"]) + 3
            else:  # Draw
                simulated_teams[home_team]["pts"] = int(simulated_teams[home_team]["pts"]) + 1
                simulated_teams[away_team]["pts"] = int(simulated_teams[away_team]["pts"]) + 1

        # Sort teams by points to determine standings
        sorted_teams = sorted(simulated_teams.values(), key=lambda x: int(x["pts"]), reverse=True)

        # Update statistics based on the custom positions provided
        for position_name, position_range in positions.items():
            start, end = position_range["start"], position_range["end"]

            # Handle the case where 'end' is None (which implies till the end of the list)
            end = len(sorted_teams) if end is None else end

            for idx, team in enumerate(sorted_teams[start:end]):
                results[team["name"]][position_name] += 1

    # Convert counts to probabilities
    for team in results:
        for position_name, position_data in positions.items():
            results[team][f"{position_name}_prob"] = results[team][position_name] / n_iterations
            results[team][f"{position_name}_description"] = position_data["description"]


    results =  format_results(results, teams)

    return results


def format_results(results, teams):
    formatted_results = []
    team_id_map = {team['shortName']: team['id'] for team in teams}

    for team, data in results.items():
        # Create a dictionary for each team
        formatted_team_data = {'team': team}
        formatted_team_data['id'] = team_id_map.get(team, None)

        # Loop over each position and add its description and probability to the dictionary
        # Loop over each position and add its description and probability to the dictionary
        for position_name in ['champion', 'layer1', 'layer2', 'layer3', 'relegated']:
            # Check if the position exists in the data
            if f'{position_name}_description' in data and f'{position_name}_prob' in data:
                description_key = data[f'{position_name}_description']
                prob_key = f"{data[f'{position_name}_prob'] * 100:.1f}%"

                # Add to the team's data dictionary
                formatted_team_data[description_key] = prob_key

        # Add the team's formatted data to the list
        formatted_results.append(formatted_team_data)
        sorted_results = sorted(
            formatted_results,
            key=lambda x: (percent_to_float(x['Champion']),
                           percent_to_float(x['ContinentalLeague']),
                           percent_to_float(x['secondContLeague'])),
            reverse=True  # Sort in descending order
        )


    return sorted_results

def percent_to_float(percent_str):
    return float(percent_str.strip('%'))  # Convert '9.0%' to 9.0

# Sort the list


def get_last_5_home_away_wins(team_name, played_games):
    # Initialize counters and lists to store the last 5 home and away results
    home_wins = 0
    away_wins = 0
    home_games_played = 0
    away_games_played = 0
    home_games = []
    away_games = []

    # Iterate through the games in reverse to prioritize the most recent ones
    for game in reversed(played_games):
        # Parse the score (e.g., '1 - 1' becomes [1, 1])
        home_score, away_score = map(int, game["score"].split(" - "))

        # Check if the team played at home
        if game["home"] == team_name and home_games_played < 5:
            home_games.append(game)
            home_games_played += 1
            # Check if it was a win for the home team
            if home_score > away_score:
                home_wins += 1
        # Check if the team played away
        elif game["away"] == team_name and away_games_played < 5:
            away_games.append(game)
            away_games_played += 1
            # Check if it was a win for the away team
            if away_score > home_score:
                away_wins += 1

        # Stop once we have the last 5 home and away games
        if home_games_played == 5 and away_games_played == 5:
            break

    # Calculate win percentages
    home_win_percentage = (home_wins / home_games_played) if home_games_played > 0 else 0
    away_win_percentage = (away_wins / away_games_played) if away_games_played > 0 else 0

    # Return the number of wins and win percentages for home and away games
    """    
        {
        "team": team_name,
        "home_win_percentage": home_win_percentage,
        "away_win_percentage": away_win_percentage
    }
    """
    return home_win_percentage, away_win_percentage


